import type { ImageResource } from '@/shared/types/common'
import { ImageWithFallback } from './image-with-fallback'

type GroupImageProps = {
  image?: ImageResource | null
  name: string
  className?: string
}

const DEFAULT_GROUP_IMAGE =
  'data:image/svg+xml;base64,'

export function GroupImage({ image, name, className }: GroupImageProps) {
  const imageUrl = image?.url ?? DEFAULT_GROUP_IMAGE

  return <ImageWithFallback src={imageUrl} alt={name} className={className} />
}
